SET NAMES latin1;
SET FOREIGN_KEY_CHECKS = 0;

CREATE TABLE `ArtGame` (
  `ArtID` int(11) NOT NULL default '0',
  `GameID` int(11) NOT NULL default '0',
  PRIMARY KEY  (`ArtID`,`GameID`),
  KEY `ArtID` (`ArtID`),
  KEY `GameID` (`GameID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `Articles` (
  `ArtID` int(11) NOT NULL auto_increment,
  `Headline` varchar(255) NOT NULL default '',
  `Body` text NOT NULL,
  `Summary` varchar(255) NOT NULL default '',
  `DateAdded` date NOT NULL default '0000-00-00',
  `UserID` int(11) NOT NULL default '0',
  `Active` int(4) NOT NULL default '0',
  `E3year` int(4) NOT NULL default '0',
  `ArtCat` varchar(255) NOT NULL default '',
  `image1` varchar(255) NOT NULL default '',
  `image2` varchar(255) NOT NULL default '',
  `image3` varchar(255) NOT NULL default '',
  `image4` varchar(255) NOT NULL default '',
  `image5` varchar(255) NOT NULL default '',
  `image6` varchar(255) NOT NULL default '',
  `image7` varchar(255) NOT NULL default '',
  `image8` varchar(255) NOT NULL default '',
  `image9` varchar(255) NOT NULL default '',
  `image10` varchar(255) NOT NULL default '',
  PRIMARY KEY  (`ArtID`),
  KEY `ArtCat` (`ArtCat`)
) ENGINE=MyISAM AUTO_INCREMENT=275 DEFAULT CHARSET=latin1;


CREATE TABLE `ConsNews` (
  `NewsID` int(11) NOT NULL default '0',
  `Console` varchar(5) NOT NULL default '',
  UNIQUE KEY `Console` (`Console`,`NewsID`),
  KEY `NewsID` (`NewsID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `ConsPR` (
  `PRID` int(11) NOT NULL default '0',
  `Console` varchar(5) NOT NULL default '',
  UNIQUE KEY `OnlyOnce` (`PRID`,`Console`),
  KEY `PRID` (`PRID`),
  KEY `Console` (`Console`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `DevNews` (
  `DevID` int(11) NOT NULL default '0',
  `NewsID` int(11) NOT NULL default '0'
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `DevPR` (
  `PRID` int(11) NOT NULL default '0',
  `DevID` int(11) NOT NULL default '0',
  UNIQUE KEY `OnlyOnce` (`PRID`,`DevID`),
  KEY `PRID` (`PRID`),
  KEY `DevID` (`DevID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `Developers` (
  `DevID` int(10) NOT NULL auto_increment,
  `Name` varchar(200) NOT NULL default '',
  `URL` varchar(200) default NULL,
  PRIMARY KEY  (`DevID`),
  UNIQUE KEY `Name` (`Name`)
) ENGINE=MyISAM AUTO_INCREMENT=1054 DEFAULT CHARSET=latin1;


CREATE TABLE `Features` (
  `GameID` int(11) NOT NULL default '0',
  `480P` tinyint(1) NOT NULL default '0',
  `720P` tinyint(1) NOT NULL default '0',
  `1080I` tinyint(1) NOT NULL default '0',
  `dolby` tinyint(1) NOT NULL default '0',
  `soundtrack` tinyint(1) NOT NULL default '0',
  `onlineplay` tinyint(1) NOT NULL default '0',
  `dlc` tinyint(1) NOT NULL default '0',
  `scoreboard` tinyint(1) NOT NULL default '0',
  `splitscreen` tinyint(1) NOT NULL default '0',
  `systemlink` tinyint(1) NOT NULL default '0',
  `players` tinyint(1) NOT NULL default '1',
  `arcadestick` tinyint(1) NOT NULL default '0',
  `lightgun` tinyint(1) NOT NULL default '0',
  `wheel` tinyint(1) NOT NULL default '0',
  PRIMARY KEY  (`GameID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `GameNews` (
  `GameID` int(11) NOT NULL default '0',
  `NewsID` int(11) NOT NULL default '0',
  UNIQUE KEY `GameID` (`GameID`,`NewsID`),
  KEY `GameID_2` (`GameID`),
  KEY `NewsID` (`NewsID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `GamePR` (
  `PRID` int(11) NOT NULL default '0',
  `GameID` int(11) NOT NULL default '0',
  UNIQUE KEY `OnlyOnce` (`PRID`,`GameID`),
  KEY `PRID` (`PRID`),
  KEY `GameID` (`GameID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `Games` (
  `GameID` int(11) NOT NULL auto_increment,
  `Title` varchar(255) default '',
  `Console` varchar(255) default '',
  `DevID` int(11) default NULL,
  `PubID` int(11) default NULL,
  `SiteURL` varchar(255) default NULL,
  `BoximagePath` varchar(255) default NULL,
  `Description` text,
  `ESRB` varchar(255) default NULL,
  `GenreID` int(11) default '0',
  `AddedBy` int(11) default '0',
  `created_at` date default NULL,
  `updated_at` date default NULL,
  PRIMARY KEY  (`GameID`),
  UNIQUE KEY `NoDups` (`Title`,`Console`),
  KEY `Title` (`Title`)
) ENGINE=MyISAM AUTO_INCREMENT=6586 DEFAULT CHARSET=latin1;


CREATE TABLE `Genres` (
  `GenreID` int(11) NOT NULL auto_increment,
  `TYPE` char(16) NOT NULL default '',
  PRIMARY KEY  (`GenreID`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=latin1;


CREATE TABLE `News` (
  `NewsID` int(11) NOT NULL auto_increment,
  `Headline` varchar(255) NOT NULL default '',
  `FrontPage` varchar(26) NOT NULL default '',
  `Body` text NOT NULL,
  `Summary` varchar(255) NOT NULL default '',
  `DateAdded` datetime NOT NULL default '0000-00-00 00:00:00',
  `UserID` int(8) NOT NULL default '0',
  `E3year` int(4) NOT NULL default '0',
  `Active` int(4) NOT NULL default '0',
  PRIMARY KEY  (`NewsID`),
  KEY `E3year` (`E3year`),
  KEY `DateActive` (`DateAdded`,`Active`),
  KEY `Active` (`Active`),
  KEY `test` (`NewsID`,`DateAdded`,`Active`)
) ENGINE=MyISAM AUTO_INCREMENT=9861 DEFAULT CHARSET=latin1;


CREATE TABLE `PressReleases` (
  `PRID` int(11) NOT NULL auto_increment,
  `Headline` varchar(255) NOT NULL default '',
  `Subhead` varchar(255) default NULL,
  `Body` text NOT NULL,
  `Summary` text,
  `DateAdded` date NOT NULL default '0000-00-00',
  `UserID` int(11) default '0',
  PRIMARY KEY  (`PRID`)
) ENGINE=MyISAM AUTO_INCREMENT=6156 DEFAULT CHARSET=latin1;


CREATE TABLE `PubNews` (
  `PubID` int(11) NOT NULL default '0',
  `NewsID` int(11) NOT NULL default '0',
  PRIMARY KEY  (`NewsID`),
  UNIQUE KEY `PubID` (`PubID`,`NewsID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `PubPR` (
  `PRID` int(11) NOT NULL default '0',
  `PubID` int(11) NOT NULL default '0',
  UNIQUE KEY `OnlyOnce` (`PRID`,`PubID`),
  KEY `PRID` (`PRID`),
  KEY `PubID` (`PubID`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `Publishers` (
  `PubID` int(10) NOT NULL auto_increment,
  `Name` varchar(200) NOT NULL default '',
  `URL` varchar(200) default NULL,
  PRIMARY KEY  (`PubID`),
  UNIQUE KEY `Name` (`Name`)
) ENGINE=MyISAM AUTO_INCREMENT=285 DEFAULT CHARSET=latin1;


CREATE TABLE `ReleaseDates` (
  `GameID` int(11) NOT NULL default '0',
  `Released` tinyint(1) NOT NULL default '0',
  `ReleaseDate` date default NULL,
  `Gold` tinyint(1) NOT NULL default '0',
  `GoldDate` date default NULL,
  `COMMENT` text,
  PRIMARY KEY  (`GameID`),
  KEY `ReleaseIndex` (`ReleaseDate`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;


CREATE TABLE `Reviews` (
  `ReviewID` int(11) NOT NULL auto_increment,
  `GameID` int(11) NOT NULL default '0',
  `Rank` tinyint(4) default NULL,
  `UserID` int(11) NOT NULL default '0',
  `Active` int(4) NOT NULL default '0',
  `DateAdded` date NOT NULL default '0000-00-00',
  `Introduction` text,
  `Screenshot1` varchar(255) default NULL,
  `GraphicScore` tinyint(4) default NULL,
  `GraphicComment` varchar(255) default NULL,
  `GraphicText` text,
  `Screenshot2` varchar(255) default NULL,
  `SoundScore` tinyint(4) default NULL,
  `SoundComment` varchar(255) default NULL,
  `SoundText` text,
  `Screenshot3` varchar(255) default NULL,
  `ControlScore` tinyint(4) default NULL,
  `ControlComment` varchar(255) default NULL,
  `ControlText` text,
  `Screenshot4` varchar(255) default NULL,
  `GameplayScore` tinyint(4) default NULL,
  `GameplayComment` varchar(255) default NULL,
  `GameplayText` text,
  `Screenshot5` varchar(255) default NULL,
  `ValueScore` tinyint(4) default NULL,
  `ValueComment` varchar(255) default NULL,
  `ValueText` text,
  `Screenshot6` varchar(255) default NULL,
  `Conclusion` text,
  `Pros` text,
  `Cons` text,
  `GameWas` varchar(10) default 'Provided',
  PRIMARY KEY  (`ReviewID`),
  KEY `GameIndex` (`GameID`),
  KEY `DateIndex` (`DateAdded`)
) ENGINE=MyISAM AUTO_INCREMENT=880 DEFAULT CHARSET=latin1;


CREATE TABLE `Users` (
  `userID` int(8) NOT NULL auto_increment,
  `username` varchar(20) NOT NULL default '',
  `password` varchar(20) NOT NULL default '',
  `firstname` varchar(30) NOT NULL default '',
  `lastname` varchar(30) NOT NULL default '',
  `status` varchar(10) NOT NULL default '',
  `email` varchar(100) default 'news@consolegold.com',
  `last_login_ip` varchar(15) default NULL,
  `last_login_host` varchar(100) NOT NULL default '',
  `last_login_dt` datetime NOT NULL default '0000-00-00 00:00:00',
  PRIMARY KEY  (`userID`),
  KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=76 DEFAULT CHARSET=latin1;


CREATE TABLE `screenshots` (
  `shotID` bigint(20) NOT NULL auto_increment,
  `filepath` varchar(255) NOT NULL default '',
  `filename` varchar(255) NOT NULL default '',
  `caption` text NOT NULL,
  `GameID` int(11) NOT NULL default '0',
  `DateAdded` date NOT NULL default '0000-00-00',
  `E3year` int(4) NOT NULL default '2006',
  PRIMARY KEY  (`shotID`),
  KEY `GameID` (`GameID`),
  KEY `dateadded` (`DateAdded`),
  KEY `gamedate` (`GameID`,`DateAdded`)
) ENGINE=MyISAM AUTO_INCREMENT=46786 DEFAULT CHARSET=latin1;


SET FOREIGN_KEY_CHECKS = 1;
